import React from "react";
import PropTypes from "prop-types";
import lionImage from "./images/lion.jpg";
import gorillaImage from "./images/gorilla.jpg";
import zebraImage from "./images/zebra.jpg";

function AnimalCard({ name, scientificName, size, diet, additional }) {
  const getImage = () => {
    switch (name) {
      case "Lion":
        return lionImage;
      case "Gorilla":
        return gorillaImage;
      case "Zebra":
        return zebraImage;
      default:
        return null;
    }
  };

  const showAdditional = (additional) => {
    const alertInformation = Object.entries(additional)
      .map(([key, value]) => `${key}: ${value}`)
      .join("\n");
    alert(alertInformation);
  };

  return (
    <div
      style={{
        border: "1px solid #ccc",
        margin: "10px",
        padding: "10px",
        width: "300px",
        display: "inline-block",
      }}
    >
      <img
        src={getImage()}
        alt={name}
        style={{ width: "100%", height: "200px", objectFit: "cover" }}
      />
      <h2>{name}</h2>
      <p>Scientific Name: {scientificName}</p>
      <p>{size} kg</p>
      <p>{diet.join(", ")}.</p>
      <button onClick={() => showAdditional(additional)}>More Info</button>
    </div>
  );
}

AnimalCard.propTypes = {
  name: PropTypes.string.isRequired,
  scientificName: PropTypes.string.isRequired,
  size: PropTypes.number.isRequired,
  diet: PropTypes.arrayOf(PropTypes.string).isRequired,
  additional: PropTypes.shape({
    notes: PropTypes.string,
    link: PropTypes.string,
  }),
};

AnimalCard.defaultProps = {
  additional: {
    notes: "No Additional Information",
    link: "No Additional Information",
  },
};

export default AnimalCard;
