import React, { useState, useEffect } from "react";
import { Card, Button, Row, Col, Form } from "react-bootstrap";
import axios from "axios";
import { Link } from "react-router-dom";

function LaptopManagement() {
  const [laptops, setLaptops] = useState([]);

  useEffect(() => {
    axios
      .get("http://localhost:5000/Laptops") // Sửa tên mảng thành Products
      .then((response) => setLaptops(response.data))
      .catch((error) => console.error("Error fetching laptops:", error));
  }, []);

  return (
    <div className="container mt-5">
      <h2 className="text-center">Laptop List</h2>
      <Form.Control type="text" placeholder="Search" className=" mr-sm-2" />

      <Row>
        {laptops.map((laptop) => (
          <Col key={laptop.id} xs={12} sm={6} md={4} lg={3} className="mb-4">
            <Card>
              <Card.Img
                variant="top"
                src={`http://localhost:5000${laptop.image}`} // Sửa đường dẫn ảnh
                style={{ height: "200px", objectFit: "cover" }}
              />
              <Card.Body>
                <Card.Title>
                  {laptop.brand} {laptop.model}
                </Card.Title>
                <Card.Text>Year: {laptop.year}</Card.Text>
                <Card.Text>
                  Price: ${laptop.price.replace("$", "")} {/* Xóa dấu $ */}
                </Card.Text>
                <Link to={`/laptops/${laptop.id}`}>
                  <Button variant="info">View Details</Button>
                </Link>
              </Card.Body>
            </Card>
          </Col>
        ))}
      </Row>
    </div>
  );
}

export default LaptopManagement;
