import React, { useState, useEffect } from "react";
import { useParams, useNavigate } from "react-router-dom";
import { Card, Button, Row, Col } from "react-bootstrap";
import axios from "axios";

function LaptopDetails() {
  const { id } = useParams();
  const [laptops, setlaptops] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    axios
      .get(`http://localhost:5000/Laptops/${id}`) // Sửa tên mảng thành Products
      .then((response) => setlaptops(response.data))
      .catch((error) => console.error("Error fetching laptop:", error));
  }, [id]);

  if (!laptop) {
    return <div>Loading...</div>;
  }

  return (
    <div className="container mt-5">
      <Row className="justify-content-center">
        <Col xs={12} md={8} lg={6}>
          <Card>
            <Card.Img
              variant="top"
              src={`http://localhost:5000${laptop.image}`} // Sửa đường dẫn ảnh
            />
            <Card.Body>
              <Card.Title>
                {laptop.brand} {laptop.model}
              </Card.Title>
              <Card.Text>Year: {laptop.year} </Card.Text>
              <Card.Text>
                Price: ${laptop.price.replace("$", "")}
              </Card.Text>{" "}
              {/* Xóa dấu $ */}
              <Card.Text>Description: </Card.Text>
              <Button variant="secondary" onClick={() => navigate("/laptops")}>
                Back to Laptop List
              </Button>
            </Card.Body>
          </Card>
        </Col>
      </Row>
    </div>
  );
}

export default LaptopDetails;
