// src/components/Home.js
import React from "react";
import { Link } from "react-router-dom";
import Carousel from "react-bootstrap/Carousel";

function Home() {
  return (
    <div style={{ position: "relative" }}>
      <Carousel>
        <Carousel.Item>
          <img
            className="d-block w-100"
            src="/images/slide.jpg" // Thay bằng tên file slide chính
            alt="Celebration Ham Slide"
          />
        </Carousel.Item>
        {/* Thêm các slide khác nếu cần */}
        {/* <Carousel.Item>
          <img
            className="d-block w-100"
            src="/images/slide2.jpg"
            alt="Second slide"
          />
        </Carousel.Item> */}
      </Carousel>

      <p
        style={{
          position: "absolute",
          bottom: "10px",
          left: "10px",
          color: "red",
        }}
      >
        This is Home Page
      </p>

      {/* Hiển thị các hình tròn nhỏ từ menu */}
      <div
        style={{
          display: "flex",
          justifyContent: "center",
          marginTop: "20px",
        }}
      >
        <img
          src="/images/menu1.jpg" // Thay bằng tên file menu 1
          alt="Menu 1"
          style={{
            width: "80px",
            height: "80px",
            borderRadius: "50%",
            margin: "0 10px",
          }}
        />
        <img
          src="/images/menu2.jpg" // Thay bằng tên file menu 2
          alt="Menu 2"
          style={{
            width: "80px",
            height: "80px",
            borderRadius: "50%",
            margin: "0 10px",
          }}
        />
        <img
          src="/images/menu3.jpg" // Thay bằng tên file menu 3
          alt="Menu 3"
          style={{
            width: "80px",
            height: "80px",
            borderRadius: "50%",
            margin: "0 10px",
          }}
        />
        <img
          src="/images/menu4.jpg" // Thay bằng tên file menu 4
          alt="Menu 4"
          style={{
            width: "80px",
            height: "80px",
            borderRadius: "50%",
            margin: "0 10px",
          }}
        />
        <img
          src="/images/menu5.jpg" // Thay bằng tên file menu 5
          alt="Menu 5"
          style={{
            width: "80px",
            height: "80px",
            borderRadius: "50%",
            margin: "0 10px",
          }}
        />
        <img
          src="/images/menu6.jpg" // Thay bằng tên file menu 6
          alt="Menu 6"
          style={{
            width: "80px",
            height: "80px",
            borderRadius: "50%",
            margin: "0 10px",
          }}
        />
      </div>

      <p style={{ marginTop: "20px" }}>
        Ready to test your knowledge? <Link to="/quiz">Start the Quiz</Link>
      </p>
    </div>
  );
}

export default Home;
