    },
    {
      name: "Nguyen Van A",
      age: 18,
      grade: "12A",
      image: "https://via.placeholder.com/150",
    },
  ];
  return (
    <div className="App">
      <Header />
      <Container className="mt-4">
        <Row>
          {students.map((student, index) => (
            <StudentCard key={index} {...student} />
          ))}
        </Row>
      </Container>
      <Footer />
    </div>
  );
}

export default App;
