import React, { useState } from "react";
import { Card, Form, Button } from "react-bootstrap";

function StudentCard(props) {
  const { name, id, address, image } = props; // Nhận image từ props
  const [status, setStatus] = useState(null);

  const handleStatusChange = (e) => {
    setStatus(e.target.value);
  };

  const handleSubmit = () => {
    // Xử lý submit ở đây. Ví dụ: gửi dữ liệu lên server
    console.log("Submitted:", { name, id, address, status });
    alert("Thông tin đã được lưu!"); // Hoặc xử lý theo cách bạn muốn
  };

  return (
    <Card>
      <Card.Body>
        <Card.Title>{name}</Card.Title>
        <Card.Text>ID: {id}</Card.Text>
        <Card.Text>Địa chỉ: {address}</Card.Text> {/* Hiển thị địa chỉ */}
        <Form.Group>
          <Form.Label>Trạng thái:</Form.Label>
          <div className="d-flex">
            {" "}
            {/* Sử dụng d-flex để xếp radio button ngang hàng */}
            <Form.Check
              type="radio"
              label="Absent"
              value="Absent"
              name="status"
              id="absent"
              onChange={handleStatusChange}
              checked={status === "Absent"} // Check radio button nếu status là "Absent"
            />
            <Form.Check
              type="radio"
              label="Present"
              value="Present"
              name="status"
              id="present"
              className="ml-3" // Thêm khoảng cách bên trái cho radio button "Present"
              onChange={handleStatusChange}
              checked={status === "Present"} // Check radio button nếu status là "Present"
            />
          </div>
        </Form.Group>
        <Button variant="warning" onClick={handleSubmit}>
          Submit
        </Button>
      </Card.Body>
    </Card>
  );
}

export default StudentCard;
