import React, { useState } from "react";
import { Card, Form, Button, Image, Row, Col } from "react-bootstrap"; // Import Row, Col

function StudentCard(props) {
  const { name, id, address, image } = props;
  const [status, setStatus] = useState(null);

  const handleStatusChange = (event) => {
    setStatus(event.target.value);
  };

  const handleSubmit = () => {
    console.log("Submitted:", { name, id, address, status });
    alert("Thông tin đã được lưu!");
  };

  return (
    <Card>
      <Card.Body>
        <Row>
          {" "}
          {/* Row for image and info */}
          <Col
            xs={12}
            md={4}
            className="d-flex justify-content-center align-items-center"
          >
            {" "}
            {/* Image takes 4 columns on medium screens */}
            <Image
              src={image}
              alt={name}
              style={{ width: "150px", height: "200px", objectFit: "cover" }} // Larger, rectangular image
            />
          </Col>
          <Col xs={12} md={8}>
            {" "}
            {/* Info takes 8 columns on medium screens */}
            <Card.Title className="text-center">{id}</Card.Title>
            <div className="d-flex justify-content-between">
              {" "}
              {/* Row for name and address */}
              <Card.Text>{name}</Card.Text>
              <Card.Text>{address}</Card.Text>
            </div>
            <Form.Group>
              <div className="d-flex justify-content-around">
                <Form.Check
                  type="radio"
                  label="Absent"
                  value="Absent"
                  name="status"
                  id={`absent-${id}`}
                  onChange={handleStatusChange}
                  checked={status === "Absent"}
                />
                <Form.Check
                  type="radio"
                  label="Present"
                  value="Present"
                  name="status"
                  id={`present-${id}`}
                  onChange={handleStatusChange}
                  checked={status === "Present"}
                />
              </div>
            </Form.Group>
            <div className="d-flex justify-content-center">
              <Button variant="warning" onClick={handleSubmit}>
                Submit
              </Button>
            </div>
          </Col>
        </Row>
      </Card.Body>
    </Card>
  );
}

export default StudentCard;
